'use strict';(function(){const indexCfg={cache:true};indexCfg.doc={id:'id',field:['title','content'],store:['title','href'],};const index=FlexSearch.create('balance',indexCfg);window.bookSearchIndex=index;index.add({'id':0,'href':'/study-document/docs/office/excel/','title':"Excel",'content':"Excel "});index.add({'id':1,'href':'/study-document/docs/office/ppt/','title':"PPT",'content':"PPT "});index.add({'id':2,'href':'/study-document/docs/office/word/','title':"word",'content':"word "});index.add({'id':3,'href':'/study-document/docs/storage/imagehost/','title':"图床",'content':"图床 图床选择 GitHub + jsdelivr 上传图床软件 PicGo 参考官网：https://github.com/Molunerfinn/PicGo\nvs-picgo https://github.com/PicGo/vs-picgo\n"});index.add({'id':4,'href':'/study-document/docs/note/','title':"笔记",'content':"笔记 "});index.add({'id':5,'href':'/study-document/docs/note/hugo/theme/academic/','title':"hugo-academic",'content':""});index.add({'id':6,'href':'/study-document/docs/type/markdown/','title':"markdown",'content':"markdown 图片大小设置 \u0026lt;img src=\u0026quot;xxx\u0026quot; width=\u0026quot;30%\u0026quot; height=\u0026quot;30%\u0026quot;\u0026gt;\n"});index.add({'id':7,'href':'/study-document/docs/note/hugo/config/','title':"配置",'content':"Hugo 配置 参考：https://themes.gohugo.io/hugo-book\n"});index.add({'id':8,'href':'/study-document/docs/note/hugo/theme/hugo-book/','title':"hugo-book",'content':"hugo-book 配置 Site Configuration # (Optional) Set Google Analytics if you use it to track your website. # Always put it on the top of the configuration file, otherwise it won\u0026#39;t work googleAnalytics = \u0026#34;UA-XXXXXXXXX-X\u0026#34; # (Optional) If you provide a Disqus shortname, comments will be enabled on # all pages. disqusShortname = \u0026#34;my-site\u0026#34; # (Optional) Set this to true if you use capital letters in file names disablePathToLower = true # (Optional) Set this to true to enable \u0026#39;Last Modified by\u0026#39; date and git author # information on \u0026#39;doc\u0026#39; type pages. enableGitInfo = true # (Optional) Theme is intended for documentation use, therefore it doesn\u0026#39;t render taxonomy. # You can remove related files with config below disableKinds = [\u0026#39;taxonomy\u0026#39;, \u0026#39;taxonomyTerm\u0026#39;] [params] # (Optional, default true) Controls table of contents visibility on right side of pages. # Start and end levels can be controlled with markup.tableOfContents setting. # You can also specify this parameter per page in front matter. BookToC = true # (Optional, default none) Set the path to a logo for the book. If the logo is # /static/logo.png then the path would be \u0026#39;logo.png\u0026#39; BookLogo = \u0026#39;logo.png\u0026#39; # (Optional, default none) Set leaf bundle to render as side menu # When not specified file structure and weights will be used BookMenuBundle = \u0026#39;/menu\u0026#39; # (Optional, default docs) Specify section of content to render as menu # You can also set value to \u0026#34;*\u0026#34; to render all sections to menu BookSection = \u0026#39;docs\u0026#39; # Set source repository location. # Used for \u0026#39;Last Modified\u0026#39; and \u0026#39;Edit this page\u0026#39; links. BookRepo = \u0026#39;https://github.com/alex-shpak/hugo-book\u0026#39; # Enable \u0026#39;Edit this page\u0026#39; links for \u0026#39;doc\u0026#39; page type. # Disabled by default. Uncomment to enable. Requires \u0026#39;BookRepo\u0026#39; param. # Path must point to \u0026#39;content\u0026#39; directory of repo. BookEditPath = \u0026#39;edit/master/exampleSite/content\u0026#39; # (Optional, default January 2, 2006) Configure the date format used on the pages # - In git information # - In blog posts BookDateFormat = \u0026#39;Jan 2, 2006\u0026#39; # (Optional, default true) Enables search function with flexsearch, # Index is built on fly, therefore it might slowdown your website. # Configuration for indexing can be adjusted in i18n folder per language. BookSearch = true # (Optional, default true) Enables comments template on pages # By default partals/docs/comments.html includes Disqus template # See https://gohugo.io/content-management/comments/#configure-disqus # Can be overwritten by same param in page frontmatter BookComments = true Page Configuration # Set type to \u0026#39;docs\u0026#39; if you want to render page outside of configured section or if you render section other than \u0026#39;docs\u0026#39; type = \u0026#39;docs\u0026#39; # Set page weight to re-arrange items in file-tree menu (if BookMenuBundle not set) weight = 10 # (Optional) Set to mark page as flat section in file-tree menu (if BookMenuBundle not set) bookFlatSection = true # (Optional, Experimental) Set to hide nested sections or pages at that level. Works only with file-tree menu mode bookCollapseSection = true # (Optional) Set true to hide page or section from side menu (if BookMenuBundle not set) bookHidden = true # (Optional) Set \u0026#39;false\u0026#39; to hide ToC from page bookToC = true # (Optional) If you have enabled BookComments for the site, you can disable it for specific pages. bookComments = true 参考：https://themes.gohugo.io/hugo-book\n"});index.add({'id':9,'href':'/study-document/docs/note/hugo/theme/','title':"主题",'content':"Hugo 主题 hugo-academic 适合作为个人主页，内容比较丰富，尤其适合作为技术或科研人员的博客。\nhugo-book  适合记笔记，或者写书。我很喜欢他右侧的页面目录，类似语雀。 其实语雀编辑体验很好，尤其是最近改版后允许用户直接在左侧目录操作新增页面。 不使用语雀，而使用 github pages，最关键也可以说唯一的原因就是对其他平台不放心，内容还是自己保管比较好。 不然万一哪天平台下线，只是一个道歉页面，而作为免费用户，只能自己想办法导出笔记，想想还是算了。  参考：https://themes.gohugo.io\n"});index.add({'id':10,'href':'/study-document/docs/office/','title':"办公",'content':"办公 "});index.add({'id':11,'href':'/study-document/docs/type/pdf/','title':"pdf",'content':"pdf "});index.add({'id':12,'href':'/study-document/docs/note/codelabs/','title':"Codelabs",'content':"Codelabs 简介 Google Developers Codelabs 中国内地访问 clmirror.storage.googleapis.com\n Hello Istio Codelab(with Google Kubernetes Engine)  googlecodelabs/tools Codelabs management \u0026amp; hosting tools\n"});index.add({'id':13,'href':'/study-document/docs/type/epub/','title':"epub",'content':"epub "});index.add({'id':14,'href':'/study-document/docs/note/hugo/','title':"Hugo",'content':"Hugo 快速上手 简介 参考官网：https://gohugo.io\n"});index.add({'id':15,'href':'/study-document/docs/type/','title':"文档类型",'content':"文档类型 "});index.add({'id':16,'href':'/study-document/docs/type/mobi/','title':"mobi",'content':"mobi mobi vs azw3 "});index.add({'id':17,'href':'/study-document/docs/type/azw3/','title':"azw3",'content':"azw3 azw3 vs azw "});index.add({'id':18,'href':'/study-document/docs/note/onenote/','title':"OneNote",'content':"OneNote "});index.add({'id':19,'href':'/study-document/docs/storage/','title':"存储",'content':"存储 "});index.add({'id':20,'href':'/study-document/docs/tool/','title':"工具",'content':"工具 格式转换 富文本 -\u0026gt; markdown  euangoddard/clipboard2markdown  http://euangoddard.github.io/clipboard2markdown/    pandoc https://pandoc.org/\n"});index.add({'id':21,'href':'/study-document/categories/','title':"Categories",'content':""});index.add({'id':22,'href':'/study-document/docs/','title':"Docs",'content':""});index.add({'id':23,'href':'/study-document/tags/','title':"Tags",'content':""});index.add({'id':24,'href':'/study-document/','title':"首页",'content':""});})();